package com.example.test.Config;import org.springframework.security.core.context.SecurityContextHolder;import org.springframework.security.core.userdetails.UserDetails;public class UserDetailsID {    public static String getEmail(){        UserDetails userDetails = (UserDetails) SecurityContextHolder.getContext().getAuthentication().getPrincipal();        System.out.println(userDetails);        return userDetails.getUsername();    }}