package com.example.test.Controllers;import com.example.test.Config.UserDetailsID;import com.example.test.Request.CommentRequest;import com.example.test.Services.CommentService;import lombok.RequiredArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RequiredArgsConstructor@RequestMapping("/api/v1/comment")@RestControllerpublic class CommentController {    private final CommentService commentService;    @PostMapping("/{postId}")    @CrossOrigin(origins = "http://localhost:3000")    public ResponseEntity<Object> comment(@PathVariable Long postId, @RequestBody CommentRequest comment){        CommentRequest commentRequest = commentService.commentPost(comment,postId, UserDetailsID.getEmail());        return new ResponseEntity<>(commentRequest, HttpStatus.CREATED);    }}