package com.example.test.Controllers;import com.example.test.Request.CommentRequest;import com.example.test.Services.CommentService;import lombok.RequiredArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.security.core.context.SecurityContextHolder;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.web.bind.annotation.*;@RequiredArgsConstructor@RequestMapping("/api/v1/comment")@RestController@CrossOrigin("localhost:3000")public class CommentController {    private final CommentService commentService;    @PostMapping("/{postId}")    public ResponseEntity<Object> comment(@PathVariable Long postId, @RequestBody CommentRequest comment){        UserDetails userDetails = (UserDetails) SecurityContextHolder.getContext().getAuthentication().getPrincipal();        CommentRequest commentRequest = commentService.commentPost(comment,postId,userDetails.getUsername());        return new ResponseEntity<>(commentRequest, HttpStatus.CREATED);    }}