package com.example.test.Controllers;import com.example.test.Request.LikeRequest;import com.example.test.Response.Response;import com.example.test.ServiceImpl.LikeServiceImplementation;import lombok.RequiredArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.security.core.context.SecurityContextHolder;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("api/v1/like")@RequiredArgsConstructorpublic class LikePostController {    private final LikeServiceImplementation likeServiceImplementation;    @PostMapping("/{postId}")    @CrossOrigin(origins = "http://localhost:3000")    public ResponseEntity<Response> Like(@PathVariable Long postId,                                         @RequestBody LikeRequest like){        UserDetails userDetails = (UserDetails) SecurityContextHolder.getContext().getAuthentication().getPrincipal();        LikeRequest likes = likeServiceImplementation.likes(postId,like,userDetails.getUsername());          return ResponseEntity.status(HttpStatus.CREATED).body(                Response.builder()                        .data(likes)                        .message((likes!=null) ? "SUCCESS" : "ERROR")                        .build()        );    }}