package com.example.test.Controllers;import com.example.test.Request.RegisterRequest;import com.example.test.Request.UserRequest;import com.example.test.Response.Response;import com.example.test.ServiceImpl.PostServiceImplemtation;import com.example.test.ServiceImpl.UserServiceImplentation;import jakarta.servlet.http.HttpServletRequest;import jakarta.servlet.http.HttpServletResponse;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.io.IOException;@RequiredArgsConstructor@RestController@CrossOrigin("localhost:3000")@RequestMapping("/api/v1/user")@Slf4jpublic class UserController {    private final UserServiceImplentation service;    private final PostServiceImplemtation postService;    @PostMapping("/create")    public ResponseEntity<Response> register(@RequestBody RegisterRequest request) {        return ResponseEntity.ok(service.register(request));    }    @PostMapping("/authenticate")    public ResponseEntity<Response> authenticate(@RequestBody UserRequest request) {        return new ResponseEntity<>(service.authenticate(request), HttpStatus.OK);    }    @PostMapping("/refresh-token")    public void refreshToken(            HttpServletRequest request,            HttpServletResponse response    ) throws IOException {        service.refreshToken(request, response);    }}