package com.example.test.Entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.time.LocalDateTime;@NoArgsConstructor@AllArgsConstructor@Builder@Entity@Datapublic class Comment {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long commentId;    private String comment;    private LocalDateTime time;    @ManyToOne    @JoinColumn(name ="id")    private User user;    @OneToOne    private Post post;}