package com.example.test.Entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.util.List;@Entity@Table(name = "post")@NoArgsConstructor@AllArgsConstructor@Builder@Datapublic class Post {    @Id    @GeneratedValue    private  Long postId;    private String message;    private String title;    private String content;    @ManyToOne    private User user;    @OneToMany    private List<Comment> commentList;}