package com.example.test.Repositories;import com.example.test.Entity.Likes;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import java.util.Optional;public interface LikeRespository extends JpaRepository<Likes,Long> {    @Query(value = "SELECT * FROM likes WHERE post_id=?1 and id=?2",nativeQuery = true)    Optional<Likes> findLikesByUserIdAndPost(Long post_id,Long userid);}