package com.example.test.ServiceImpl;import com.example.test.Entity.Post;import com.example.test.Entity.User;import com.example.test.Enums.Role;import com.example.test.Exceptions.PostNotFoundException;import com.example.test.Repositories.PostRepository;import com.example.test.Repositories.UserRepository;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.modelmapper.ModelMapper;import org.springframework.stereotype.Service;@Service@RequiredArgsConstructor@Slf4jpublic class AdminImplementationService {    private final UserRepository userRepository;    private final PostRepository postRepository;    private final ModelMapper modelMapper;    public void deletePost(Long id,String email){        User find = userRepository.findUsersByEmail(email);        Post post = postRepository.findById(id).orElse(null);        if(find.getRole().equals(Role.ADMIN)){            postRepository.delete(post);        }else{            throw new PostNotFoundException(id);        }    }}